  UNIT_AMOUNT VARCHAR2(1 CHAR),
  SOURCE_BANK_ACCOUNT_ID VARCHAR2 (1 CHAR),
  DEST_BANK_ACCOUNT_ID VARCHAR2 (1 CHAR),
